mapper.select:
select
  account_id, account_IDnumber, account_card, account_openbank, account_pwd, account_userid
from js_bank_account.secondaccount


        and account_id = #{accountId}


        and account_IDnumber = #{accountIdnumber}


        and account_card = #{accountCard}


        and account_openbank = #{accountOpenbank}


        and account_pwd = #{accountPwd}


        and account_userid = #{accountUserid}
