mapper.select:
select
  batch_id, batch_name, batch_number, batch_site, batch_price, batch_phone, batch_payname, batch_paynumber, batch_payuserid, batch_currency
from js_bank_account.batch


        and batch_id = #{batchId}


        and batch_name = #{batchName}


        and batch_number = #{batchNumber}


        and batch_site = #{batchSite}


        and batch_price = #{batchPrice}


        and batch_phone = #{batchPhone}


        and batch_payname = #{batchPayname}


        and batch_paynumber = #{batchPaynumber}


        and batch_payuserid = #{batchPayuserid}


        and batch_currency = #{batchCurrency}
