mapper.select:
select
  makeremittance_id, makeremittance_serialnumber, makeremittance_time, makeremittance_price, makeremittance_paynumber, makeremittance_name, makeremittance_number, makeremittance_payuserid, makeremittance_payname, makeremittance_branch
from js_bank_account.makeremittance


        and makeremittance_id = #{makeremittanceId}


        and makeremittance_serialnumber = #{makeremittanceSerialnumber}


        and makeremittance_time = #{makeremittanceTime}


        and makeremittance_price = #{makeremittancePrice}


        and makeremittance_paynumber = #{makeremittancePaynumber}


        and makeremittance_name = #{makeremittanceName}


        and makeremittance_number = #{makeremittanceNumber}


        and makeremittance_payuserid = #{makeremittancePayuserid}


        and makeremittance_payname = #{makeremittancePayname}


        and makeremittance_branch = #{makeremittanceBranch}
