mapper.select:
select
  remittancetransaction_id, remittancetransaction_name, remittancetransaction_cardnumber, remittancetransaction_blank, remittancetransaction_price, remittancetransaction_time, remittancetransaction_current, remittancetransaction_number, remittancetransaction_payuserid
from js_bank_account.remittancetransaction


        and remittancetransaction_id = #{remittancetransactionId}


        and remittancetransaction_name = #{remittancetransactionName}


        and remittancetransaction_cardnumber = #{remittancetransactionCardnumber}


        and remittancetransaction_blank = #{remittancetransactionBlank}


        and remittancetransaction_price = #{remittancetransactionPrice}


        and remittancetransaction_time = #{remittancetransactionTime}


        and remittancetransaction_current = #{remittancetransactionCurrent}


        and remittancetransaction_number = #{remittancetransactionNumber}


        and remittancetransaction_payuserid = #{remittancetransactionPayuserid}
